mapper.select:
WITH VW_LINE_CNT AS (
/* 진행 상태 정보 가져오기 */ SELECT APRV_NO , CASE WHEN TOT_CNT = APRV_YN_0 THEN '0' WHEN TOT_CNT = APRV_YN_1 THEN '1' WHEN APRV_YN_2 > 0 THEN '2' WHEN APRV_YN_3 > 0 THEN '3' ELSE '9' END APRV_LINE_CD FROM ( SELECT A.APRV_NO , SUM(TOT_CNT) As TOT_CNT , SUM(APRV_YN_0) AS APRV_YN_0 /* 대기 */ , SUM(APRV_YN_1) AS APRV_YN_1 /* 완료 */ , SUM(APRV_YN_2) AS APRV_YN_2 /* 보류 */ , SUM(APRV_YN_3) AS APRV_YN_3 /* 반려 */ FROM ( SELECT APRV_NO , COUNT(1) AS TOT_CNT , CASE WHEN APRV_YN_CD = 0 AND NVL(REFE_YN, 'N') = 'N' THEN COUNT(1) ELSE 0 END APRV_YN_0 /* 대기 */ , CASE WHEN APRV_YN_CD = 1 AND NVL(REFE_YN, 'N') = 'N' THEN COUNT(1) ELSE 0 END APRV_YN_1 /* 완료 */ , CASE WHEN APRV_YN_CD = 2 AND NVL(REFE_YN, 'N') = 'N' THEN COUNT(1) ELSE 0 END APRV_YN_2 /* 보류 */ , CASE WHEN APRV_YN_CD = 3 AND NVL(REFE_YN, 'N') = 'N' THEN COUNT(1) ELSE 0 END APRV_YN_3 /* 반려 */ FROM STD_APRV_LINE WHERE NVL(REFE_YN, 'N') = 'N' GROUP BY APRV_NO, APRV_YN_CD ,NVL(REFE_YN, 'N') ) A GROUP BY A.APRV_NO ) ) , VW_CONF_Y AS ( SELECT APRV_NO , CASE WHEN CONF_TOT = CONF_N THEN 'N' ELSE 'Y' END CONF_TYPE FROM ( SELECT APRV_NO , SUM(CONF_TOT) AS CONF_TOT , SUM(CONF_N) AS CONF_N FROM ( SELECT APRV_NO , COUNT(1) AS CONF_TOT , CASE WHEN CONF_YN = 'N' THEN COUNT(1) ELSE 0 END CONF_N FROM STD_APRV_LINE WHERE APRV_NO = #{aprv_no} AND NVL(REFE_YN, 'N') = 'N' GROUP BY APRV_NO, CONF_YN ) GROUP BY APRV_NO ) ) , VW_ORGNZT AS ( SELECT USS.USS_ID , DP.DP_NM FROM STD_USS_MNG USS LEFT OUTER JOIN ( SELECT CD AS DP_CD, LTRIM (SYS_CONNECT_BY_PATH (CD_NM, ' > '), ' > ') AS DP_NM FROM STD_CD_MNG START WITH UP_CD = 'CD0008' CONNECT BY PRIOR CD = UP_CD ORDER SIBLINGS BY CD_ORD ASC ) DP ON DP.DP_CD = USS.DP_CD ) SELECT A.APRV_NO /* 결재번호 */ , A.REPT_APRV_NO /* 보고자사원번호 */ , B.USS_NM AS REPT_APRV_NM , A.REPT_AUTH_CD AS REPT_AUTH_CD /* 보고자 직급 코드 */ , (SELECT AUTHOR_NM FROM STD_AUT_INFO WHERE AUTHOR_CODE = A.REPT_AUTH_CD) AS REPT_AUTH_NM /* 보고자 직급 명 */ , A.REPT_DP_CD AS REPT_DP_CD /* 보고자 부서 코드 */ , (SELECT CD_NM FROM STD_CD_MNG WHERE CD = A.REPT_DP_CD) AS REPT_DP_NM /* 보고자 부서 명 */ , A.APRV_TYPE_CD /* 결재구분코드(UP_CD=VarConsts.EAM_MASTER_CODE) */ , A.APRV_TYPE_DTIL_CD /* 결재구분상세코드 */ , A.TITLE /* 제목 */ , A.PROJ_CD /* 프로젝트코드 */ , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = A.PROJ_CD) AS PROJ_NM /*프로젝트 명칭*/ , A.TERM_ST_YM /* 기간시작일자 */ , A.TERM_ED_YM /* 기간종료일자 */ , A.TERM_ST_HM /* 기간시작시간 */ , A.TERM_ED_HM /* 기간종료시간 */ , SUBSTR(A.TERM_ST_HM, 1, 2) TERM_ST_H , SUBSTR(A.TERM_ST_HM, 3, 4) TERM_ST_M , SUBSTR(A.TERM_ED_HM, 1, 2) TERM_ED_H , SUBSTR(A.TERM_ED_HM, 3, 4) TERM_ED_M , A.REPT_CONT /* 보고내용 */ , A.HALF_TYPE_CD /* 시작일자반차구분코드 */ , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = A.HALF_TYPE_CD) AS HALF_TYPE_CD_ST_NM /*시작일자반차구분명칭*/ , A.HALF_TYPE_CD_ED /* 종료일자반차구분코드 */ , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = A.HALF_TYPE_CD_ED) AS HALF_TYPE_CD_ED_NM /*종료일자반차구분명칭*/ , A.PLACE /* 행선지 */ , NVL(C.APRV_LINE_CD, '0') AS APRV_LINE_CD , D.CONF_TYPE /* 확인 유무 */ , E.DP_NM AS dpNm , A.CAL_NO /* 캘린더 번호 */ , TO_CHAR(CRTN_DT, 'YYYY-MM-DD') CRTN_DT /* 작성일 */
, F.FROGH_RSN /*휴가사유*/ , F.ADDR /*행선지*/ , F.CTTPLC /*연락처*/ , G.VCT_DAY_CNT /*휴가사용일수*/ , A.CANCEL_YN /*관리자취소여부*/ , A.CRTN_EMP_NO /*결재문서작성자*/
FROM STD_APRV_MNG A LEFT OUTER JOIN STD_USS_MNG B ON B.USS_ID = A.REPT_APRV_NO LEFT OUTER JOIN VW_LINE_CNT C ON C.APRV_NO = A.APRV_NO LEFT OUTER JOIN VW_CONF_Y D ON D.APRV_NO = A.APRV_NO LEFT OUTER JOIN VW_ORGNZT E ON E.USS_ID = A.REPT_APRV_NO LEFT OUTER JOIN DOC_FROGH F ON F.APRV_NO = A.APRV_NO LEFT OUTER JOIN ( SELECT APRV_NO , SUM(VCT_DAY_CNT) AS VCT_DAY_CNT FROM VCT_INF_MNG WHERE USE_YN = 'Y' GROUP BY APRV_NO ) G ON G.APRV_NO = A.APRV_NO WHERE A.APRV_NO = #{aprv_no}

