  APRV_YN_CD = '3'
WHERE APRV_NO = #{aprv_no}
    AND APRV_ORDR < (
          SELECT 
              MAX(APRV_ORDR) 
          FROM STD_APRV_LINE 
          WHERE APRV_NO = #{aprv_no} 
              AND APRV_EMP_NO = #{aprv_emp_no}
    )

