mapper.select:
SELECT cd, cd_nm, up_cd, levl, cd_val FROM STD_CD_MNG WHERE levl = '1'
AND cd != #{notcode}
ORDER BY cd

SELECT cd ,cd_nm ,up_cd ,levl ,cd_ord ,cd_val FROM STD_CD_MNG WHERE up_cd = #{up_cd, javaType=String ,jdbcType=VARCHAR}
AND CD_VAL = #{cd_val}
ORDER BY cd_ord

mapper.insert:

	SELECT 
		'CD' ||
		SUBSTR('0000' || TO_CHAR(NVL(MAX(TO_NUMBER(SUBSTR(cd,-4))),0)+1),-4)
	FROM STD_CD_MNG WHERE up_cd = #{up_cd}

INSERT INTO STD_CD_MNG (
	 cd
	,cd_nm
	,up_cd
	,levl
	,cd_val
	,cd_ord
)VALUES(
	#{cd}
	,#{cd_nm}
	,#{up_cd}
	,#{levl}
	,#{cd_val}
	,#{cd_ord}
)


	SELECT 
		#{up_cd} ||
		SUBSTR('000' || TO_CHAR(NVL(MAX(TO_NUMBER(SUBSTR(cd,-3))),0)+1),-3)
	FROM STD_CD_MNG WHERE up_cd = #{up_cd}

INSERT INTO STD_CD_MNG (
	 cd
	,cd_nm
	,up_cd
	,levl
	,cd_val
	,cd_ord
)VALUES(
	#{cd}
	,#{cd_nm}
	,#{up_cd}
	,#{levl}
	,#{cd_val}
	,#{cd_ord}
)

mapper.select:
SELECT TO_CHAR(MAX(TO_NUMBER(cd_ord))) MAX_ORDR
FROM STD_CD_MNG    
WHERE up_cd = #{cd}

mapper.update:
UPDATE STD_CD_MNG
SET cd_nm = #{cd_nm},
cd_val = #{cd_val}
WHERE cd = #{cd}

mapper.select:
SELECT 
		cd_nm 
	FROM 
		STD_CD_MNG 
		WHERE up_cd = #{up_cd}
		AND cd_val = #{cd_val}
