mapper.update:
UPDATE STD_ERROR_LOG
   SET PROCESS_STATE = #{process_state}
 WHERE ERROR_SEQ = #{error_seq}

UPDATE STD_ERROR_LOG
   SET PROCESS_STATE = #{process_state}
     , PROCESS_DATE = sysdate
     , PROCESS_REG_NM = #{process_reg_nm}
     , PROCESS_CONTENT = #{process_content}
 WHERE ERROR_SEQ = #{error_seq}

UPDATE STD_ERROR_LOG
   SET PROCESS_STATE = #{process_state}
     , PROCESS_CONTENT = #{process_content}
 WHERE ERROR_SEQ = #{error_seq}
