mapper.insert:
INSERT INTO STD_USS_MNG(USS_ID, USS_PWD, USS_NM, USS_SEX, USS_BIRTH, USS_EMAIL, USS_TEL, USS_AUTH_CD, REG_DT, USS_EM_NO, DP_CD)
  VALUES(#{uss_id}, #{uss_pwd}, #{uss_nm}, #{uss_sex}, #{uss_birth, jdbcType=VARCHAR}, #{uss_email, jdbcType=VARCHAR}
  		, #{uss_tel, jdbcType=VARCHAR}, #{uss_auth_cd, jdbcType=VARCHAR}, SYSDATE, #{uss_em_no, jdbcType=VARCHAR}, #{dp_cd, jdbcType=VARCHAR})

mapper.select:
SELECT A.USS_ID , A.USS_NM , A.USS_BIRTH , A.USS_SEX , A.USS_TEL , A.USS_EMAIL
, B.AUTHOR_CODE AS USS_AUTH_CD , CASE WHEN A.USS_SEX = 'M' THEN '남자' WHEN A.USS_SEX = 'W' THEN '여자' ELSE '' END USS_SEX_VAL , A.USS_EM_NO , C.CD_NM AS USS_AUTH_NM , A.DP_CD , D.DP_NM FROM STD_USS_MNG A
LEFT OUTER JOIN STD_AUT_ROLE_MAP B ON A.USS_ID = B.SCRTY_DTRMN_TRGET_ID LEFT OUTER JOIN STD_CD_MNG C ON C.UP_CD IN ('CD0005', 'CD0006') AND C.CD_VAL = B.AUTHOR_CODE LEFT OUTER JOIN ( SELECT CD AS DP_CD, LTRIM (SYS_CONNECT_BY_PATH (CD_NM, ' > '), ' > ') AS DP_NM FROM STD_CD_MNG START WITH UP_CD = 'CD0008' CONNECT BY PRIOR CD = UP_CD ORDER SIBLINGS BY CD_ORD ASC ) D ON D.DP_CD = A.DP_CD WHERE A.USS_ID = #{uss_id}
AND A.USS_PWD = #{uss_pwd}

mapper.update:
UPDATE std_uss_mng SET
	uss_nm = #{uss_nm}
	, uss_birth = #{uss_birth, jdbcType=VARCHAR}
	, uss_sex = #{uss_sex, jdbcType=VARCHAR}
	, uss_tel = #{uss_tel, jdbcType=VARCHAR}
	, uss_email = #{uss_email, jdbcType=VARCHAR}
	, uss_em_no = #{uss_em_no, jdbcType=VARCHAR}
	, uss_auth_cd = #{uss_auth_cd, jdbcType=VARCHAR}
	, dp_cd = #{dp_cd, jdbcType=VARCHAR}

	, uss_pwd = #{uss_pwd}

WHERE uss_id = #{uss_id}
