mapper.select:
SELECT A.USS_ID , A.USS_NM , A.USS_BIRTH , A.USS_SEX , A.USS_TEL , A.USS_EMAIL
, B.AUTHOR_CODE AS USS_AUTH_CD , CASE WHEN A.USS_SEX = 'M' THEN '남자' WHEN A.USS_SEX = 'W' THEN '여자' ELSE '' END USS_SEX_VAL , A.USS_EM_NO , C.CD_NM AS USS_AUTH_NM , A.DP_CD , D.DP_NM
, A.JOIN_DT /*입사일*/ , A.RTR_DT /*퇴사일*/ , A.RTR_YN /*퇴사여부*/ , A.USS_BIRTH_DAY /*생일(월-일)*/ , A.USS_BIRTH_DAY_TYPE /*생일 음/양력 구분*/ , A.WORK_YR_CNT /*근속년수*/ , A.EMP_TYPE /*고용구분*/ , E.CD_NM /*고용구분 명칭*/ AS EMP_TYPE_NM FROM STD_USS_MNG A
LEFT OUTER JOIN STD_AUT_ROLE_MAP B ON A.USS_ID = B.SCRTY_DTRMN_TRGET_ID LEFT OUTER JOIN STD_CD_MNG C ON C.UP_CD IN ('CD0005', 'CD0006') AND C.CD_VAL = B.AUTHOR_CODE LEFT OUTER JOIN ( SELECT CD AS DP_CD, LTRIM (SYS_CONNECT_BY_PATH (CD_NM, ' > '), ' > ') AS DP_NM FROM STD_CD_MNG START WITH UP_CD = 'CD0008' CONNECT BY PRIOR CD = UP_CD ORDER SIBLINGS BY CD_ORD ASC ) D ON D.DP_CD = A.DP_CD LEFT OUTER JOIN STD_CD_MNG E ON (E.CD = A.EMP_TYPE) WHERE A.USS_ID = #{uss_id}
AND A.USS_PWD = #{uss_pwd}

mapper.update:
UPDATE std_uss_mng SET
	uss_nm = #{uss_nm}
	, uss_birth = #{uss_birth, jdbcType=VARCHAR}
	, uss_sex = #{uss_sex, jdbcType=VARCHAR}
	, uss_tel = #{uss_tel, jdbcType=VARCHAR}
	, uss_email = #{uss_email, jdbcType=VARCHAR}
	, uss_em_no = #{uss_em_no, jdbcType=VARCHAR}
	, uss_auth_cd = #{uss_auth_cd, jdbcType=VARCHAR}
	, dp_cd = #{dp_cd, jdbcType=VARCHAR}
	
	, join_dt = #{join_dt, jdbcType=VARCHAR}
	, rtr_yn = #{rtr_yn}
	, rtr_dt = #{rtr_dt, jdbcType=VARCHAR}
	, uss_birth_day = #{uss_birth_day, jdbcType=VARCHAR}
	, uss_birth_day_type = #{uss_birth_day_type, jdbcType=VARCHAR}
	, work_yr_cnt = #{work_yr_cnt}
	, emp_type = #{emp_type, jdbcType=VARCHAR}

	, uss_pwd = #{uss_pwd}

WHERE uss_id = #{uss_id}

mapper.insert:
INSERT INTO STD_APRV_LINE_INFO(
	  EMP_NO
	, APRV_ORDR
	, REFE_YN
	, APRV_EMP_NO
) VALUES (
	  #{emp_no, jdbcType=VARCHAR}
	, #{aprv_ordr, jdbcType=VARCHAR}
	, #{refe_yn, jdbcType=VARCHAR}
	, #{aprv_emp_no, jdbcType=VARCHAR}
)
