mapper.select:
SELECT A.USS_ID /*사원ID*/ , A.USS_NM /*사원명*/ , A.JOIN_DT /*입사일*/ , A.WORK_YR_CNT /*근속년수*/ , A.RTR_DT /*퇴사일*/ , A.RTR_YN /*퇴사여부*/ , B.VCT_GRNT_DAY /*휴가부여일수*/ , #{searchGubun2} AS STDD_YR /*기준년도*/ FROM STD_USS_MNG A LEFT OUTER JOIN (
SELECT STDD_YR , USS_ID , VCT_GRNT_DAY FROM VCT_DAY_MNG WHERE STDD_YR = #{searchGubun2}
) B ON (B.USS_ID = A.USS_ID)
1=1
AND A.RTR_YN = #{searchGubun3}
AND ( A.RTR_YN IS NULL OR A.RTR_YN != 'Y' )
AND A.USS_ID LIKE '%' || #{searchField} || '%'
AND A.USS_NM LIKE '%' || #{searchField} || '%'
ORDER BY A.USS_NM ASC
