mapper.select:
SELECT A.USS_ID /*사원ID*/ , A.USS_NM /*사원명*/ , A.JOIN_DT /*입사일*/ , A.WORK_YR_CNT /*근속년수*/ , A.RTR_DT /*퇴사일*/ , A.RTR_YN /*퇴사여부*/ , B.VCT_GRNT_DAY /*휴가부여일수*/ , #{searchGubun2} AS STDD_YR /*기준년도*/ FROM STD_USS_MNG A LEFT OUTER JOIN (
SELECT STDD_YR , USS_ID , VCT_GRNT_DAY FROM VCT_DAY_MNG WHERE STDD_YR = #{searchGubun2}
) B ON (B.USS_ID = A.USS_ID)
1=1
AND ( TO_CHAR(A.REG_DT, 'YYYY') <= #{searchGubun2} OR SUBSTR(A.JOIN_DT, 1, 4) <= #{searchGubun2} )
AND A.RTR_YN = #{searchGubun3}
AND ( A.RTR_YN IS NULL OR A.RTR_YN != 'Y' )
AND A.USS_ID LIKE '%' || #{searchField} || '%'
AND A.USS_NM LIKE '%' || #{searchField} || '%'
ORDER BY A.USS_NM ASC

SELECT A.USS_ID , A.USS_NM , A.JOIN_DT , A.REG_DT , A.RTR_YN , A.RTR_DT , A.WORK_YR_CNT - ( TO_NUMBER(TO_CHAR(SYSDATE, 'YYYY')) - TO_NUMBER(#{searchGubun2}) ) AS WORK_YR_CNT , A.EMP_TYPE , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = A.EMP_TYPE) AS EMP_TYPE_NM , B.APRV_NO , B.PROJ_CD , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = B.PROJ_CD) AS PROJ_NM , B.REPT_DP_CD AS DP_CD , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = B.REPT_DP_CD) AS DP_NM , B.REPT_AUTH_CD AS AUTH_CD , (SELECT S.AUTHOR_NM FROM STD_AUT_INFO S WHERE S.AUTHOR_CODE = B.REPT_AUTH_CD) AS AUTH_NM , #{searchGubun2} AS STDD_YR FROM STD_USS_MNG A LEFT OUTER JOIN (
SELECT C.APRV_NO , C.REPT_APRV_NO , C.PROJ_CD , C.REPT_DP_CD , C.REPT_AUTH_CD FROM STD_APRV_MNG C INNER JOIN ( SELECT REPT_APRV_NO , MAX(TO_NUMBER(APRV_NO)) AS MAX_APRV_NO FROM STD_APRV_MNG WHERE TO_CHAR(MODI_DT, 'YYYY') = #{searchGubun2} GROUP BY REPT_APRV_NO ORDER BY MAX(TO_NUMBER(APRV_NO)) DESC ) D ON (C.APRV_NO = D.MAX_APRV_NO)
) B ON (A.USS_ID = B.REPT_APRV_NO)
1=1 AND ( TO_CHAR(A.REG_DT, 'YYYY') <= #{searchGubun2} OR SUBSTR(A.JOIN_DT, 1, 4)<= #{searchGubun2} ) /*입사년도*/
AND A.RTR_YN = #{searchGubun3}
AND ( A.RTR_YN IS NULL OR A.RTR_YN != 'Y' )
AND A.USS_ID LIKE '%' || #{searchField} || '%'
AND A.USS_NM LIKE '%' || #{searchField} || '%'
AND A.EMP_TYPE = #{searchGubun4}
AND B.REPT_AUTH_CD = #{searchGubun5}
AND B.PROJ_CD = #{searchGubun6}
ORDER BY A.USS_NM ASC

WITH W_PARAMS AS( SELECT #{searchGubun2} AS STDD_YR FROM DUAL ),W_USS_LIST AS ( SELECT B.STDD_YR , A.USS_ID , A.USS_NM , A.EMP_TYPE , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = A.EMP_TYPE) AS EMP_TYPE_NM , A.RTR_YN , DECODE(A.RTR_YN, 'Y', '퇴직', '재직') AS RTR_YN_NM , A.JOIN_DT , A.RTR_DT , A.WORK_YR_CNT , C.VCT_GRNT_DAY FROM STD_USS_MNG A LEFT OUTER JOIN W_PARAMS B ON (1=1) LEFT OUTER JOIN VCT_DAY_MNG C ON (A.USS_ID = C.USS_ID AND B.STDD_YR = C.STDD_YR) WHERE 1=1 AND ( TO_CHAR(A.REG_DT, 'YYYY') <= B.STDD_YR OR SUBSTR(A.JOIN_DT, 1, 4) <= B.STDD_YR )/*기준년도 이전에 입사한 사원*/
AND A.RTR_YN = #{searchGubun3}
AND ( NVL(SUBSTR(A.RTR_DT, 1, 4), '9999') >= B.STDD_YR ) /*현재 재직중인 사원*/
AND A.EMP_TYPE = #{searchGubun4}
AND A.USS_ID LIKE '%' || #{searchField} || '%'
AND A.USS_NM LIKE '%' || #{searchField} || '%'
),W_LAST_APRV AS ( SELECT C.REPT_APRV_NO , MAX(TO_NUMBER(C.APRV_NO)) AS MAX_APRV_NO FROM STD_APRV_MNG C LEFT OUTER JOIN W_PARAMS D ON (1=1) WHERE TO_CHAR(C.CRTN_DT, 'YYYY') = D.STDD_YR GROUP BY C.REPT_APRV_NO ),W_USS_ADD_INFO AS( SELECT E.APRV_NO , E.REPT_APRV_NO , E.PROJ_CD , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = E.PROJ_CD) AS PROJ_NM , E.REPT_DP_CD , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = E.REPT_DP_CD) AS REPT_DP_NM , E.REPT_AUTH_CD , (SELECT S.AUTHOR_NM FROM STD_AUT_INFO S WHERE S.AUTHOR_CODE = E.REPT_AUTH_CD) AS REPT_AUTH_NM FROM STD_APRV_MNG E INNER JOIN W_LAST_APRV F ON (E.APRV_NO = F.MAX_APRV_NO )
),W_VCT_TYPE_LIST AS ( SELECT * FROM STD_CD_MNG WHERE UP_CD = 'CD0001011' ORDER BY CD_ORD ), W_VCT_SUM_DAY AS ( SELECT G.USS_ID , G.VCT_TYPE , SUM(G.VCT_DAY_CNT) AS VCT_USE_DAY FROM VCT_INF_MNG G LEFT OUTER JOIN W_PARAMS H ON (1=1) INNER JOIN (SELECT APRV_NO FROM STD_APRV_MNG WHERE CANCEL_YN IS NULL OR CANCEL_YN != 'Y') I ON (G.APRV_NO = I.APRV_NO) WHERE G.VCT_YR = H.STDD_YR AND G.USE_YN = 'Y' GROUP BY G.USS_ID, G.VCT_TYPE ), W_VCT_USE_DAY_CNT AS (
SELECT J.STDD_YR , J.USS_ID , J.USS_NM , J.EMP_TYPE , J.EMP_TYPE_NM , J.RTR_YN , J.RTR_YN_NM , J.JOIN_DT , J.RTR_DT , J.WORK_YR_CNT , J.VCT_GRNT_DAY , K.CD AS VCT_TYPE , K.CD_NM AS VCT_TYPE_NM , K.CD_ORD , NVL(I.VCT_USE_DAY, 0) AS VCT_USE_DAY , J.VCT_GRNT_DAY - NVL(I.VCT_USE_DAY, 0) AS VCT_LEFT_DAY , NVL(I.VCT_USE_DAY / J.VCT_GRNT_DAY * 100, 0) AS VCT_USE_RATE FROM W_USS_LIST J LEFT OUTER JOIN W_VCT_TYPE_LIST K ON (1=1) LEFT OUTER JOIN W_VCT_SUM_DAY I ON (I.USS_ID = J.USS_ID AND K.CD = I.VCT_TYPE) ORDER BY J.USS_NM, K.CD_ORD
), W_VCT_USS_INFO AS( SELECT L.STDD_YR , L.USS_ID , L.USS_NM , L.EMP_TYPE , L.EMP_TYPE_NM , L.RTR_YN , L.RTR_YN_NM , L.JOIN_DT , L.RTR_DT , NVL(L.WORK_YR_CNT, 0) AS WORK_YR_CNT , NVL(L.VCT_GRNT_DAY, 0) AS VCT_GRNT_DAY , M.PROJ_CD , M.PROJ_NM , M.REPT_DP_CD , M.REPT_DP_NM , M.REPT_AUTH_CD , M.REPT_AUTH_NM
FROM W_USS_LIST L LEFT OUTER JOIN W_USS_ADD_INFO M ON (M.REPT_APRV_NO = L.USS_ID)
1=1
AND M.REPT_AUTH_CD = #{searchGubun5}
AND M.PROJ_CD = #{searchGubun6}
) SELECT COUNT(1) AS cnt FROM W_VCT_USS_INFO

WITH W_PARAMS AS( SELECT #{searchGubun2} AS STDD_YR FROM DUAL ),W_USS_LIST AS ( SELECT B.STDD_YR , A.USS_ID , A.USS_NM , A.EMP_TYPE , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = A.EMP_TYPE) AS EMP_TYPE_NM , A.RTR_YN , DECODE(A.RTR_YN, 'Y', '퇴직', '재직') AS RTR_YN_NM , A.JOIN_DT , A.RTR_DT , A.WORK_YR_CNT , C.VCT_GRNT_DAY FROM STD_USS_MNG A LEFT OUTER JOIN W_PARAMS B ON (1=1) LEFT OUTER JOIN VCT_DAY_MNG C ON (A.USS_ID = C.USS_ID AND B.STDD_YR = C.STDD_YR) WHERE 1=1 AND ( TO_CHAR(A.REG_DT, 'YYYY') <= B.STDD_YR OR SUBSTR(A.JOIN_DT, 1, 4) <= B.STDD_YR )/*기준년도 이전에 입사한 사원*/
AND A.RTR_YN = #{searchGubun3}
AND ( NVL(SUBSTR(A.RTR_DT, 1, 4), '9999') >= B.STDD_YR ) /*현재 재직중인 사원*/
AND A.EMP_TYPE = #{searchGubun4}
AND A.USS_ID LIKE '%' || #{searchField} || '%'
AND A.USS_NM LIKE '%' || #{searchField} || '%'
),W_LAST_APRV AS ( SELECT C.REPT_APRV_NO , MAX(TO_NUMBER(C.APRV_NO)) AS MAX_APRV_NO FROM STD_APRV_MNG C LEFT OUTER JOIN W_PARAMS D ON (1=1) WHERE TO_CHAR(C.CRTN_DT, 'YYYY') = D.STDD_YR GROUP BY C.REPT_APRV_NO ),W_USS_ADD_INFO AS( SELECT E.APRV_NO , E.REPT_APRV_NO , E.PROJ_CD , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = E.PROJ_CD) AS PROJ_NM , E.REPT_DP_CD , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = E.REPT_DP_CD) AS REPT_DP_NM , E.REPT_AUTH_CD , (SELECT S.AUTHOR_NM FROM STD_AUT_INFO S WHERE S.AUTHOR_CODE = E.REPT_AUTH_CD) AS REPT_AUTH_NM FROM STD_APRV_MNG E INNER JOIN W_LAST_APRV F ON (E.APRV_NO = F.MAX_APRV_NO )
),W_VCT_TYPE_LIST AS ( SELECT * FROM STD_CD_MNG WHERE UP_CD = 'CD0001011' ORDER BY CD_ORD ), W_VCT_SUM_DAY AS ( SELECT G.USS_ID , G.VCT_TYPE , SUM(G.VCT_DAY_CNT) AS VCT_USE_DAY FROM VCT_INF_MNG G LEFT OUTER JOIN W_PARAMS H ON (1=1) INNER JOIN (SELECT APRV_NO FROM STD_APRV_MNG WHERE CANCEL_YN IS NULL OR CANCEL_YN != 'Y') I ON (G.APRV_NO = I.APRV_NO) WHERE G.VCT_YR = H.STDD_YR AND G.USE_YN = 'Y' GROUP BY G.USS_ID, G.VCT_TYPE ), W_VCT_USE_DAY_CNT AS (
SELECT J.STDD_YR , J.USS_ID , J.USS_NM , J.EMP_TYPE , J.EMP_TYPE_NM , J.RTR_YN , J.RTR_YN_NM , J.JOIN_DT , J.RTR_DT , J.WORK_YR_CNT , J.VCT_GRNT_DAY , K.CD AS VCT_TYPE , K.CD_NM AS VCT_TYPE_NM , K.CD_ORD , NVL(I.VCT_USE_DAY, 0) AS VCT_USE_DAY , J.VCT_GRNT_DAY - NVL(I.VCT_USE_DAY, 0) AS VCT_LEFT_DAY , NVL(I.VCT_USE_DAY / J.VCT_GRNT_DAY * 100, 0) AS VCT_USE_RATE FROM W_USS_LIST J LEFT OUTER JOIN W_VCT_TYPE_LIST K ON (1=1) LEFT OUTER JOIN W_VCT_SUM_DAY I ON (I.USS_ID = J.USS_ID AND K.CD = I.VCT_TYPE) ORDER BY J.USS_NM, K.CD_ORD
), W_VCT_USS_INFO AS( SELECT L.STDD_YR , L.USS_ID , L.USS_NM , L.EMP_TYPE , L.EMP_TYPE_NM , L.RTR_YN , L.RTR_YN_NM , L.JOIN_DT , L.RTR_DT , NVL(L.WORK_YR_CNT, 0) AS WORK_YR_CNT , NVL(L.VCT_GRNT_DAY, 0) AS VCT_GRNT_DAY , M.PROJ_CD , M.PROJ_NM , M.REPT_DP_CD , M.REPT_DP_NM , M.REPT_AUTH_CD , M.REPT_AUTH_NM
FROM W_USS_LIST L LEFT OUTER JOIN W_USS_ADD_INFO M ON (M.REPT_APRV_NO = L.USS_ID) ) SELECT X.STDD_YR , X.USS_ID , X.USS_NM , X.EMP_TYPE , X.EMP_TYPE_NM , X.RTR_YN , X.RTR_YN_NM , X.JOIN_DT , X.RTR_DT , X.WORK_YR_CNT , X.PROJ_CD , X.PROJ_NM , X.REPT_DP_CD , X.REPT_DP_NM , X.REPT_AUTH_CD , X.REPT_AUTH_NM , X.VCT_GRNT_DAY AS YEON_GRNT_DAY , (SELECT S.VCT_USE_DAY FROM W_VCT_USE_DAY_CNT S WHERE S.USS_ID = X.USS_ID AND S.VCT_TYPE = 'CD0001011001') AS YEON_USE_CNT , (SELECT NVL(S.VCT_LEFT_DAY, 0) FROM W_VCT_USE_DAY_CNT S WHERE S.USS_ID = X.USS_ID AND S.VCT_TYPE = 'CD0001011001') AS YEON_LEFT_CNT , (SELECT ROUND(S.VCT_USE_RATE, 2) FROM W_VCT_USE_DAY_CNT S WHERE S.USS_ID = X.USS_ID AND S.VCT_TYPE = 'CD0001011001') AS YEON_USE_RATE , (SELECT S.VCT_USE_DAY FROM W_VCT_USE_DAY_CNT S WHERE S.USS_ID = X.USS_ID AND S.VCT_TYPE = 'CD0001011002') AS DISS_USE_CNT , (SELECT S.VCT_USE_DAY FROM W_VCT_USE_DAY_CNT S WHERE S.USS_ID = X.USS_ID AND S.VCT_TYPE = 'CD0001011003') AS FAM_EVNT_USE_CNT , (SELECT S.VCT_USE_DAY FROM W_VCT_USE_DAY_CNT S WHERE S.USS_ID = X.USS_ID AND S.VCT_TYPE = 'CD0001011004') AS REPLC_USE_CNT , (SELECT S.VCT_USE_DAY FROM W_VCT_USE_DAY_CNT S WHERE S.USS_ID = X.USS_ID AND S.VCT_TYPE = 'CD0001011005') AS CMFT_USE_CNT , (SELECT S.VCT_USE_DAY FROM W_VCT_USE_DAY_CNT S WHERE S.USS_ID = X.USS_ID AND S.VCT_TYPE = 'CD0001011006') AS FAM_CARE_USE_CNT ,(SELECT NVL(SUM(S.VCT_USE_DAY), 0) FROM W_VCT_USE_DAY_CNT S WHERE S.USS_ID = X.USS_ID) AS VCT_TOT_CNT FROM W_VCT_USS_INFO X
1=1
AND X.REPT_AUTH_CD = #{searchGubun5}
AND X.PROJ_CD = #{searchGubun6}
ORDER BY X.USS_NM

WITH W_PARAMS AS( SELECT #{searchGubun2} AS STDD_YR FROM DUAL ),W_USS_LIST AS ( SELECT B.STDD_YR , A.USS_ID , A.USS_NM , A.EMP_TYPE , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = A.EMP_TYPE) AS EMP_TYPE_NM , A.RTR_YN , DECODE(A.RTR_YN, 'Y', '퇴직', '재직') AS RTR_YN_NM , A.JOIN_DT , A.RTR_DT , A.WORK_YR_CNT , C.VCT_GRNT_DAY FROM STD_USS_MNG A LEFT OUTER JOIN W_PARAMS B ON (1=1) LEFT OUTER JOIN VCT_DAY_MNG C ON (A.USS_ID = C.USS_ID AND B.STDD_YR = C.STDD_YR) WHERE 1=1 AND ( TO_CHAR(A.REG_DT, 'YYYY') <= B.STDD_YR OR SUBSTR(A.JOIN_DT, 1, 4) <= B.STDD_YR )/*기준년도 이전에 입사한 사원*/
AND A.RTR_YN = #{searchGubun3}
AND ( NVL(SUBSTR(A.RTR_DT, 1, 4), '9999') >= B.STDD_YR ) /*현재 재직중인 사원*/
AND A.EMP_TYPE = #{searchGubun4}
AND A.USS_ID LIKE '%' || #{searchField} || '%'
AND A.USS_NM LIKE '%' || #{searchField} || '%'
),W_LAST_APRV AS ( SELECT C.REPT_APRV_NO , MAX(TO_NUMBER(C.APRV_NO)) AS MAX_APRV_NO FROM STD_APRV_MNG C LEFT OUTER JOIN W_PARAMS D ON (1=1) WHERE TO_CHAR(C.CRTN_DT, 'YYYY') = D.STDD_YR GROUP BY C.REPT_APRV_NO ),W_USS_ADD_INFO AS( SELECT E.APRV_NO , E.REPT_APRV_NO , E.PROJ_CD , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = E.PROJ_CD) AS PROJ_NM , E.REPT_DP_CD , (SELECT S.CD_NM FROM STD_CD_MNG S WHERE S.CD = E.REPT_DP_CD) AS REPT_DP_NM , E.REPT_AUTH_CD , (SELECT S.AUTHOR_NM FROM STD_AUT_INFO S WHERE S.AUTHOR_CODE = E.REPT_AUTH_CD) AS REPT_AUTH_NM FROM STD_APRV_MNG E INNER JOIN W_LAST_APRV F ON (E.APRV_NO = F.MAX_APRV_NO )
1=1
AND E.REPT_AUTH_CD = #{searchGubun5}
AND E.PROJ_CD = #{searchGubun6}
),W_VCT_TYPE_LIST AS ( SELECT * FROM STD_CD_MNG WHERE UP_CD = 'CD0001011' ORDER BY CD_ORD ), W_VCT_SUM_DAY AS ( SELECT G.USS_ID , G.VCT_TYPE , SUM(G.VCT_DAY_CNT) AS VCT_USE_DAY FROM VCT_INF_MNG G LEFT OUTER JOIN W_PARAMS H ON (1=1) INNER JOIN (SELECT APRV_NO FROM STD_APRV_MNG WHERE CANCEL_YN IS NULL OR CANCEL_YN != 'Y') I ON (G.APRV_NO = I.APRV_NO) WHERE G.VCT_YR = H.STDD_YR AND G.USE_YN = 'Y' GROUP BY G.USS_ID, G.VCT_TYPE
), W_VCT_SUM_DAY_QRTR AS ( SELECT G.USS_ID , G.VCT_TYPE , G.VCT_QRTR , SUM(G.VCT_DAY_CNT) AS VCT_USE_DAY FROM VCT_INF_MNG G LEFT OUTER JOIN W_PARAMS H ON (1=1) INNER JOIN (SELECT APRV_NO FROM STD_APRV_MNG WHERE CANCEL_YN IS NULL OR CANCEL_YN != 'Y') I ON (G.APRV_NO = I.APRV_NO) WHERE G.VCT_YR = H.STDD_YR AND G.USE_YN = 'Y' GROUP BY G.USS_ID, G.VCT_TYPE , G.VCT_QRTR ) , W_VCT_USE_DAY_CNT AS (
SELECT J.STDD_YR , J.USS_ID , J.USS_NM , J.EMP_TYPE , J.EMP_TYPE_NM , J.RTR_YN , J.RTR_YN_NM , J.JOIN_DT , J.RTR_DT , J.WORK_YR_CNT , J.VCT_GRNT_DAY , K.CD AS VCT_TYPE , K.CD_NM AS VCT_TYPE_NM , K.CD_ORD , NVL(I.VCT_USE_DAY, 0) AS VCT_USE_DAY , J.VCT_GRNT_DAY - NVL(I.VCT_USE_DAY, 0) AS VCT_LEFT_DAY , NVL(I.VCT_USE_DAY / J.VCT_GRNT_DAY * 100, 0) AS VCT_USE_RATE FROM W_USS_LIST J LEFT OUTER JOIN W_VCT_TYPE_LIST K ON (1=1) LEFT OUTER JOIN W_VCT_SUM_DAY I ON (I.USS_ID = J.USS_ID AND K.CD = I.VCT_TYPE) ORDER BY J.USS_NM, K.CD_ORD
), W_QRTR AS ( SELECT LEVEL AS QRTR FROM DUAL CONNECT BY LEVEL <= 4
) , W_VCT_USE_DAY_CNT_QRTR AS (
SELECT J.STDD_YR , J.USS_ID , J.USS_NM , J.EMP_TYPE , J.EMP_TYPE_NM , J.RTR_YN , J.RTR_YN_NM , J.JOIN_DT , J.RTR_DT , J.WORK_YR_CNT , J.VCT_GRNT_DAY , K.CD AS VCT_TYPE , K.CD_NM AS VCT_TYPE_NM , K.CD_ORD , H.QRTR AS VCT_QRTR , NVL(I.VCT_USE_DAY, 0) AS VCT_USE_DAY , J.VCT_GRNT_DAY - NVL(I.VCT_USE_DAY, 0) AS VCT_LEFT_DAY , NVL(I.VCT_USE_DAY / J.VCT_GRNT_DAY * 100, 0) AS VCT_USE_RATE FROM W_USS_LIST J LEFT OUTER JOIN W_VCT_TYPE_LIST K ON (1=1) LEFT OUTER JOIN W_QRTR H ON (1=1) LEFT OUTER JOIN W_VCT_SUM_DAY_QRTR I ON (I.USS_ID = J.USS_ID AND K.CD = I.VCT_TYPE AND H.QRTR = I.VCT_QRTR) ORDER BY J.USS_NM, H.QRTR, K.CD_ORD
)
SELECT A.CD AS VCT_TYPE_CD /*휴가 코드*/ , A.CD_NM AS VCT_TYPE_NM /*휴가 코드 명*/ , (SELECT NVL(ROUND(AVG(S.VCT_USE_DAY), 2), 0) FROM W_VCT_USE_DAY_CNT S WHERE S.VCT_TYPE = A.CD) AS TOT_AVG_USE_DAY /*총 사용일수 평균*/ , (SELECT NVL(ROUND(AVG(S.VCT_LEFT_DAY), 2), 0) FROM W_VCT_USE_DAY_CNT S WHERE S.VCT_TYPE = A.CD) AS TOT_AVG_LEFT_DAY /*총 미사용일수 평균*/ , (SELECT NVL(ROUND(AVG(S.VCT_USE_RATE), 2), 0) FROM W_VCT_USE_DAY_CNT S WHERE S.VCT_TYPE = A.CD) AS TOT_AVG_USE_RATE /*총 활용률 평균*/
, (SELECT NVL(ROUND(AVG(S.VCT_USE_DAY), 2), 0) FROM W_VCT_USE_DAY_CNT_QRTR S WHERE S.VCT_TYPE = A.CD AND S.VCT_QRTR = 1) AS FIR_AVG_USE_DAY /*1분기 사용일수 평균*/ , (SELECT NVL(ROUND(AVG(S.VCT_LEFT_DAY), 2), 0) FROM W_VCT_USE_DAY_CNT_QRTR S WHERE S.VCT_TYPE = A.CD AND S.VCT_QRTR = 1) AS FIR_AVG_LEFT_DAY /*1분기 미사용일수 평균*/ , (SELECT NVL(ROUND(AVG(S.VCT_USE_RATE), 2), 0) FROM W_VCT_USE_DAY_CNT_QRTR S WHERE S.VCT_TYPE = A.CD AND S.VCT_QRTR = 1) AS FIR_AVG_USE_RATE /*1분기 활용률 평균*/
, (SELECT NVL(ROUND(AVG(S.VCT_USE_DAY), 2), 0) FROM W_VCT_USE_DAY_CNT_QRTR S WHERE S.VCT_TYPE = A.CD AND S.VCT_QRTR = 2) AS SEC_AVG_USE_DAY /*2분기 사용일수 평균*/ , (SELECT NVL(ROUND(AVG(S.VCT_LEFT_DAY), 2), 0) FROM W_VCT_USE_DAY_CNT_QRTR S WHERE S.VCT_TYPE = A.CD AND S.VCT_QRTR = 2) AS SEC_AVG_LEFT_DAY /*2분기 미사용일수 평균*/ , (SELECT NVL(ROUND(AVG(S.VCT_USE_RATE), 2), 0) FROM W_VCT_USE_DAY_CNT_QRTR S WHERE S.VCT_TYPE = A.CD AND S.VCT_QRTR = 2) AS SEC_AVG_USE_RATE /*2분기 활용률 평균*/
, (SELECT NVL(ROUND(AVG(S.VCT_USE_DAY), 2), 0) FROM W_VCT_USE_DAY_CNT_QRTR S WHERE S.VCT_TYPE = A.CD AND S.VCT_QRTR = 3) AS THR_AVG_USE_DAY /*3분기 사용일수 평균*/ , (SELECT NVL(ROUND(AVG(S.VCT_LEFT_DAY), 2), 0) FROM W_VCT_USE_DAY_CNT_QRTR S WHERE S.VCT_TYPE = A.CD AND S.VCT_QRTR = 3) AS THR_AVG_LEFT_DAY /*3분기 미사용일수 평균*/ , (SELECT NVL(ROUND(AVG(S.VCT_USE_RATE), 2), 0) FROM W_VCT_USE_DAY_CNT_QRTR S WHERE S.VCT_TYPE = A.CD AND S.VCT_QRTR = 3) AS THR_AVG_USE_RATE /*3분기 활용률 평균*/
, (SELECT NVL(ROUND(AVG(S.VCT_USE_DAY), 2), 0) FROM W_VCT_USE_DAY_CNT_QRTR S WHERE S.VCT_TYPE = A.CD AND S.VCT_QRTR = 4) AS FRT_AVG_USE_DAY /*4분기 사용일수 평균*/ , (SELECT NVL(ROUND(AVG(S.VCT_LEFT_DAY), 2), 0) FROM W_VCT_USE_DAY_CNT_QRTR S WHERE S.VCT_TYPE = A.CD AND S.VCT_QRTR = 4) AS FRT_AVG_LEFT_DAY /*4분기 미사용일수 평균*/ , (SELECT NVL(ROUND(AVG(S.VCT_USE_RATE), 2), 0) FROM W_VCT_USE_DAY_CNT_QRTR S WHERE S.VCT_TYPE = A.CD AND S.VCT_QRTR = 4) AS FRT_AVG_USE_RATE /*4분기 활용률 평균*/ FROM W_VCT_TYPE_LIST A GROUP BY A.CD, A.CD_NM ORDER BY A.CD

