             , #{vct_type}
             , #{vct_trm_type}
             , TO_NUMBER(#{vct_qrtr})
             , #{vct_yr}
             , #{proj_cd}
             , TO_NUMBER(#{vct_day_cnt})
             , #{emp_type, jdbcType=VARCHAR}
             , #{rtr_yn, jdbcType=VARCHAR}
             , #{aut_cd}
             , SYSDATE
             , SYSDATE
             , #{use_yn, jdbcType=VARCHAR}			
		
		)

mapper.delete:
DELETE
  FROM VCT_INF_MNG
 WHERE APRV_NO = #{aprv_no, jdbcType=VARCHAR}

mapper.update:
UPDATE VCT_INF_MNG
  SET USE_YN = #{use_yn, jdbcType=VARCHAR}
 WHERE VCT_DT = #{vct_dt, jdbcType=VARCHAR}
